var selectedColor = null
function buildTileArray() {
    var allPaths = document.getElementsByTagName('path')
    var pathCount = allPaths.length // using the dot notation once to save as a variable stops referencing it 1000s of times
    for (let i = 0; i < pathCount; i++) {
        if (allPaths[i].dataset.paintable === "true" )  tileArray.push(allPaths[i])   
    }
}
buildTileArray()

var tileCount = tileArray.length


// Tile functions
function updateTiles() {
    for (let i = 0; i < tileCount; i++) { // '.length' slows down the code as it needs to be accessed each time 
        if (newColorArray[i] !== colorArray[i]) {
            colorArray[i] = newColorArray[i];
            tileArray[i].dataset.color = newColorArray[i]
            tileArray[i].setAttribute("class" ,  "f" + tileArray[i].dataset.face + "c" + colorArray[i] ) 
        }
    }
}

function renderTiles() {
    for (let i = 0; i < tileCount; i++) { 
            tileArray[i].setAttribute("class" ,  "f" + tileArray[i].dataset.face + "c" + colorArray[i] )
    }
}

function indexTiles() {
    for (let i = 0; i < tileCount; i++) {
        tileArray[i].setAttribute('data-index', "" + i + "")
    }
}

function initializeTileEvents() {
    for (let i = 0; i < tileCount; i++) {
        tileArray[i].addEventListener("mouseover",() => {
            // if (mouseDown){
                if (selectedColor !== null)
                newColorArray[i] = selectedColor
                updateTiles()

            // } 
        });
    }
}

// Screen Functions
function fadeLoader() {
    var loaderDiv = document.getElementById('fadeIn')
    loaderDiv.classList.add("ready");
    setTimeout( () => { loaderDiv.remove() }, 3000);
}

// paint functions
var currentColor1Pallete = 0
var currentColor2Pallete = 4
var color1CSSFile = document.getElementById('color1Pallete')
var color2CSSFile = document.getElementById('color2Pallete')
var changeColor1Btn = document.getElementById("changeColor1")
var changeColor2Btn = document.getElementById("changeColor2")
var color1selected = document.getElementById("selectColor1")
var color2selected = document.getElementById("selectColor2")
var color0selected = document.getElementById("selectColor0")
var cleanAllBtn = document.getElementById("cleanAll")


function updateColor1Pallete(){
    var newUrl
    if (currentColor1Pallete.toString().length < 2){
        newUrl = "0" + currentColor1Pallete + ".css"
    } else {
        newUrl = currentColor1Pallete + ".css"
    }
    color1CSSFile.href =  color1CSSFile.href.slice(0,-6) + newUrl
}

function updateColor2Pallete(){
    var newUrl;
    if (currentColor2Pallete.toString().length < 2){
        newUrl = "0" + currentColor2Pallete + ".css"
    } else {
        newUrl = currentColor2Pallete + ".css"
    }
    color2CSSFile.href =  color2CSSFile.href.slice(0,-6) + newUrl
}

function cycleColorPalleteSelection(colorPalletSelection){
    if (colorPalletSelection < 16 ){ // 16 is the total pallet count
        return colorPalletSelection + 1
    } else {
        return 0
    }
}

function deselectAllButtons() {
    var menuButtons = document.getElementsByClassName('brushMenuButtons')
    for (let i = 0; i < menuButtons.length; i++) {
       menuButtons[i].classList.remove('selected')
    }
}

function toggleDeselect(newSelectedColor){
    if (newSelectedColor === selectedColor) {
        selectedColor = null
        deselectAllButtons()
    }
}

function swapBrushes(newSelectedColor, event) {
    if (newSelectedColor === selectedColor) {
        selectedColor = null
        deselectAllButtons()
    } else {
        deselectAllButtons()
        event.target.classList.add("selected")
        selectedColor = newSelectedColor 
    }    
}


// menu event listeners
changeColor1Btn.addEventListener("click", () => {
    currentColor1Pallete = cycleColorPalleteSelection(currentColor1Pallete)
    updateColor1Pallete()
    
});

changeColor2Btn.addEventListener("click", () => {
    currentColor2Pallete = cycleColorPalleteSelection(currentColor2Pallete)
    updateColor2Pallete()
    
});


color1selected.addEventListener("click", (event) => {
    swapBrushes(1, event)
});

color2selected.addEventListener("click", (event) => {
    swapBrushes(2, event)
});

color0selected.addEventListener("click", (event) => {
    swapBrushes(0, event)
});

cleanAllBtn.addEventListener("click", () => {
    for (let i = 0; i < tileCount; i++) {
        if (tileArray[i].dataset.color !== 0) {
            newColorArray[i] = 0
            updateTiles()
        }
    }



});



//Initalizers
buildTileArray();
renderTiles()
indexTiles();
initializeTileEvents()
updateColor1Pallete()
updateColor2Pallete()

//clearLoader
fadeLoader();
















